import React, { Component } from 'react';
import './StarWarsDB.css';
import StarWarsData from './StarWarsData';

class StarWarsDB extends Component {
  state = {
    colHeaders: [],
    data: []
  }
  // constructor(props){
  //   super(props);
  //   this.state = {
  //   };
  // }

  // componentWillMount(){}
  componentDidMount() {
    let headerCells = [], entry = this.state.data[0];
    for (let k in entry) {
      headerCells.push(<td key={k}>{entry[k]}</td>);
    }
    const colHeaders = <tr>{headerCells}</tr>;

    this.setState({ 
      colHeaders: colHeaders,
      data: StarWarsData.results
    });
  }
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}

  render() {

    const tableRows = this.state.data.map((entry) => {
      let tableCells = [];
      for (let k in entry) {
        tableCells.push(<td key={k}>{entry[k]}</td>);
      }
      return (<tr key={entry.name}>{tableCells}</tr>)
    })

    return (
      <div>Use the Force!<br />
        <table>
          <thead>
            {this.state.colHeaders}
          </thead>
          <tbody>
            {tableRows}
          </tbody>
        </table>
      </div>
    );
  }
}

export default StarWarsDB;
