import React, { Component } from 'react';
import './Todo.css';

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: ""
    };
    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    console.log("Added item.", this.state.todoInput);
  }

  render() {
    return (
      <div>
        <div>This is the Todo component: {this.props.testprop}</div>
        <input type="text" value={this.state.todoInput} onChange={(e) => this.handleChange(e) } />
        <button onClick={() => this.addItem()}>Add</button>
      </div>
    );
  }
}

export default Todo;
