import React, { Component } from 'react';
import './Todo.css';

function TodoItem(props) {
  return (
    <div>
      <input type="checkbox"
        checked="{props.item.completed}"
        onChange={props.changeHandler}/> {props.item.item}
    </div>
  );
}

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: "",
      todoList: []
    };
    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    const todolist = this.state.todoList.slice();
    this.setState({
      todoList: todolist.concat([{
        item: this.state.todoInput,
        completed: false
      }]),
      todoInput: ""
    });
  }

  markItem(i) {
    console.log(i, this.state.todoList[i]);
  }

  listToJSXMap(elt,i) {
    return (
      <li key={i}>
        <TodoItem item={elt} changeHandler={() => {
        console.log(this);
        this.markItem(i)}}/>
      </li>
    );
  }

  render() {
    const todoItems = this.state.todoList
                      .filter((item) => !item.completed)
                      .map(this.listToJSXMap);

    const completedItems = this.state.todoList
                            .filter((item) => item.completed)
                            .map(this.listToJSXMap);

    return (
      <div>
        <div>This is the Todo component: {this.props.testprop}</div>
        <button onClick={() => this.markItem(1)}>mark item</button>
        <input type="text" value={this.state.todoInput} onChange={(e) => this.handleChange(e) } />
        <button onClick={() => this.addItem()}>Add</button>
        <div>
          <ul>
            {todoItems}
          </ul>
        </div>
        <div>
          <ul>
            {completedItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default Todo;
