import React, {Component} from 'react';
import './App.css';

import Todo from './Todo/Todo';
import Calculator from './Calculator/Calculator';
import Weather from './Weather/Weather';
import TeamRoster from './TeamRoster/TeamRoster';


// Import routing components
import { BrowserRouter, Route, Link, Redirect } from "react-router-dom";

// react practice projects: https://daveceddia.com/react-practice-projects/

// router example: https://reacttraining.com/react-router/web/guides/quick-start
class App extends Component {
  constructor() {
    super();
    this.state = {
      currentApp: ""
    }
  }
  appChange = (event) => {
    if (event.target.value) {
      this.setState({currentApp: event.target.value});
    }
  }

  render () {
    let redirect = "";
    if (this.state.currentApp !== "") {
      redirect = <Redirect to={"/" + this.state.currentApp} push/>;
    }

    return (
      <BrowserRouter>
        <div>
          <p><Link to="/">Home</Link></p>
          <select value={this.state.currentApp} onChange={this.appChange}>
            <option value="">Select an App</option>
            <option value="todo">Todo</option>
            <option value="calculator">Calculator</option>
            <option value="weather">Weather</option>
            <option value="roster_viewer">Rosters</option>
            <option value="star_wars">Star Wars</option>
          </select>

          <hr />

          {redirect}
          <Route path="/todo" component={Todo}/>
          <Route path="/calculator" component={Calculator}/>
          <Route path="/weather" component={Weather}/>
          <Route path="/roster_viewer" component={TeamRoster}/>
          <Route path="/star_wars" component={StarWarsDB}/>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
