import React, { Component } from 'react';
import './Todo.css';

function TodoItem(props) {
  return (<div>{props.value}</div>);
}

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: "",
      todoList: []
    };
    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    const todolist = this.state.todoList.slice();
    this.setState({ todoList: todolist.concat([this.state.todoInput]) });
  }

  render() {
    const todoItems = this.state.todoList.map((elt,i) => {
      return ( <li key={i}><TodoItem value={elt}/></li> )
    });

    return (
      <div>
        <div>This is the Todo component: {this.props.testprop}</div>
        <input type="text" value={this.state.todoInput} />
        <button onClick={() => this.addItem()}>Add</button>
        <div>
          <ul>
            {todoItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default Todo;
