import React, {Component} from 'react'

function GameOfLifeGrid(props) {
	const rows = this.props.grid.map((row, i) => {
        const cells = row.map((status, j) => {
            const classname = status ? "alive" : "dead";
            return (<td key={j} 
                className={classname} 
                onClick={() => this.props.handleCellClick(i,j)}>
                    {i},{j}
                </td> 
            )
        });
        return <tr key={i}>{cells}</tr>
    });

	return (
		<table>
            <tbody>
                {rows}
            </tbody>
        </table>
	)
}

export default GameOfLifeGrid;
