import React, {Component} from 'react';

class SingleCellContent extends Component {

  state = {
    content: "",
    isLoaded: false
  }
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     homeworld: ""
  //   }
  // }

  componentDidMount() {
    fetch(this.props.api)
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: false,
                    content: result[this.props.field]
                });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    isLoaded: true
                });
            }
        ).catch((err) => {
            console.log(err);
        })
  }

  render() {
    return (<span>{this.state.content}</span>)
  }

}

export default SingleCellContent;
