import React, { Component } from 'react';
import './Todo.css';

class TodoItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isHovering: false
    }
  }

  handleMouseEnter() { this.setState({ isHovering: true }); }
  handleMouseLeave() { this.setState({ isHovering: false }); }

  render(){
    return (
      <div
          onMouseEnter={() => this.handleMouseEnter()}
          onMouseLeave={() => this.handleMouseLeave()}>
        <input type="checkbox"
          checked={this.props.item.completed}
          onChange={this.props.changeHandler}/> {this.props.item.item}
        {
          this.state.isHovering > 0 ? <span>X</span> : null
        }
      </div>
    );
  }
}

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: "",
      //todoList: []
      todoList: [{item:"First",completed:false,index:0},{item:"Second",completed:false,index:1},{item:"Third",completed:true,index:2}]
    };

    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    const todolist = this.state.todoList.slice();
    this.setState({
      todoList: todolist.concat([{
        item: this.state.todoInput,
        completed: false,
        index: todolist.length
      }]),
      todoInput: ""
    });
  }

  markItem(item) {
    let todolist = this.state.todoList.slice();
    todolist[item.index].completed = !todolist[item.index].completed;
    this.setState({ todoList: todolist });
  }

  listToJSXMap(elt,i) {
    return (
      <li key={i}>
        <TodoItem item={elt} changeHandler={() => this.markItem(elt) }/>
      </li>
    );
  }

  render() {
    const todoItems = this.state.todoList
                      .filter((item) => !item.completed)
                      .map((elt, i) => this.listToJSXMap(elt,i));
    const completedItems = this.state.todoList
                            .filter((item) => item.completed)
                            .map((elt, i) => this.listToJSXMap(elt,i));

    return (
      <div>
        <div>This is the Todo component</div>
        <input type="text" value={this.state.todoInput} onChange={(e) => this.handleChange(e) } />
        <button onClick={() => this.addItem()}>Add</button>
        <div>
          <ul>
            {todoItems}
          </ul>
        </div>
        {
          completedItems.length > 0 ? <h3>Completed</h3> : null
        }
        <div>
          <ul>
            {completedItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default Todo;
