import React, { Component } from 'react';
import './TeamRoster.css';

const apiServer = "http://localhost:8080";

class TeamRoster extends Component {
    constructor(props){
        super(props);
        this.state = {
            roster: [],
            currentPlayerViewing: null
        };
    }

  // componentWillMount(){}
    componentDidMount() {
        fetch(apiServer+"/nba/players")
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    roster: result
                });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        ).catch((err) => {
            console.log(err);
        })
    }

    // componentWillUnmount(){}
    // componentWillReceiveProps(){}
    // shouldComponentUpdate(){}
    // componentWillUpdate(){}
    // componentDidUpdate(){}

    viewPlayerDetails(player) {
        this.setState({ currentPlayerViewing: player });
    }

    render() {

        const rosterRows = this.state.roster.map((player) => {
            return (
                <tr key={player.id}>
                    <td>{player.firstName} {player.lastName}</td>
                    <td>{player.position}</td>
                    <td><button onClick={() => this.viewPlayerDetails(player)}>Details</button></td>
                    {/*
                    <td>{player.age}</td>
                    <td>{player.height}</td>
                    <td>{player.weight}</td>
                    <td>{player.college}</td>
                    <td>{player.salary}</td>*/
                    }
                </tr>
            )
        })

        return (
        <div>
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Position</th>
                            <th></th>
                            {/*<th>Height</th>
                            <th>Weight</th>
                            <th>College</th>
                            <th>Salary</th>*/
                            }
                        </tr>
                    </thead>
                    <tbody>{rosterRows}</tbody>
                </table>
            </div>
            {
            (this.state.currentPlayerViewing !== null) ? <PlayerDetail playerId={this.state.currentPlayerViewing.playerId} player={this.state.currentPlayerViewing} /> : null
            }
        </div>
        );
    }
}

class PlayerDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            player: null
        }
    }

    componentDidMount() {
        fetch(apiServer+"/nba/players/"+this.props.playerId)
        .then(res => res.json())
        .then(

            (result) => {
                console.log("componentDidMount", result);
                this.setState({
                    isLoaded: true,
                    player: result
                });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        ).catch((err) => {
            console.log(err);
        })
    }

    render(){

        let salary = this.state.player.salary.toString();
        let salaryStr = "", charCnt = 0;
        for (let i=salary.length-1; i>=0; i--) {
            if (charCnt > 0 && charCnt % 3 === 0) {
                salaryStr = "," + salaryStr;
            }
            salaryStr =  salary.charAt(i) + salaryStr;
            charCnt++;
        }

        return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Age</th>
                        <th>Height</th>
                        <th>Weight</th>
                        <th>College</th>
                        <th>Salary</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{this.state.player.firstName} {this.props.player.lastName}</td>
                        <td>{this.state.player.position}</td>
                        <td>{this.state.player.age}</td>
                        <td>{this.state.player.height}</td>
                        <td>{this.state.player.weight}</td>
                        <td>{this.state.player.college}</td>
                        <td>${salaryStr}</td>
                    </tr>
                </tbody>
            </table>
        </div>
        );
    }
  }

export default TeamRoster;
