import React, { Component } from 'react';
import './StarWarsDB.css';

import StarWarsData from './StarWarsData';
import SingleCellContent from './SingleCellContent';

class StarWarsDB extends Component {
  state = {
    colHeaders: [],
    data: []
  }
  // constructor(props){
  //   super(props);
  //   this.state = {
  //   };
  // }

  // componentWillMount(){}
  componentDidMount() {
    let headerCells = [], entry = StarWarsData.results[0];
    for (let k in entry) {
      headerCells.push(<th key={k}>{k}</th>);
    }
    const colHeaders = <tr>{headerCells}</tr>;

    this.setState({ 
      colHeaders: colHeaders,
      data: StarWarsData.results
    });
  }
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}

  render() {

    const tableRows = this.state.data.map((entry) => {
      let tableCells = [];
      for (let k in entry) {
        let content;
        switch (k) {
          case "homeworld":
          case "species":
            content = <SingleCellContent api={entry[k]}/>;
            break;
          case "films":
            content = [];
            entry[k].forEach((film) => content.push(<SingleCellContent api={entry[k]}/>))
            break;
          default:
            content = entry[k];
        }
        tableCells.push(<td key={k}>{content}</td>);
      }
      return (<tr key={"body_"+entry.name}>{tableCells}</tr>)
    })

    return (
      <div>Use the Force!<br />
        <table>
          <thead>
            {this.state.colHeaders}
          </thead>
          <tbody>
            {tableRows}
          </tbody>
        </table>
      </div>
    );
  }
}

export default StarWarsDB;
