import React, { Component } from 'react';
import './Todo.css';

function TodoItem(props) {
  return (
    <div>
      <input type="checkbox" onChange="{props.onChange(e)}"/> {props.value}
    </div>
  );
}

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: "",
      todoList: []
    };
    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    const todolist = this.state.todoList.slice();
    this.setState({
      todoList: todolist.concat([{
        item: this.state.todoInput,
        completed: false
      }]),
      todoInput: ""
    });
  }

  markItem(i) {
    console.log(i, this.state.todoList[i]);
  }
  render() {
    const todoItems = this.state.todoList.map((elt,i) => {
      if (!elt.completed) {
        return (
          <li key={i}>
            <TodoItem value={elt.item} changeHandler={(i) => this.markItem(i)}/>
          </li>
        );
      }
    });

    const completedItems = this.state.todoList.map((elt,i) => {
      if (elt.completed) {
        return (
          <li key={i}>
            <TodoItem value={elt.item} changeHandler={(i) => this.markItem(i)}/>
          </li>
        );
      }
    });

    return (
      <div>
        <div>This is the Todo component: {this.props.testprop}</div>
        <input type="text" value={this.state.todoInput} onChange={(e) => this.handleChange(e) } />
        <button onClick={() => this.addItem()}>Add</button>
        <div>
          <ul>
            {todoItems}
          </ul>
        </div>
        <div>
          <ul>
            {completedItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default Todo;
