import React, { Component } from 'react';
import './Todo.css';

function TodoItem(props) {
  return (
    <li>
      {props.value}
    </li>
  );
}

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoInput: "",
      todoList: []
    };
    //this.handleChange = this.handleChange.bind(this);
  }

  // componentWillMount(){}
  // componentDidMount(){}
  // componentWillUnmount(){}

  // componentWillReceiveProps(){}
  // shouldComponentUpdate(){}
  // componentWillUpdate(){}
  // componentDidUpdate(){}
  handleChange(event) {
    this.setState({todoInput: event.target.value});
  }

  addItem() {
    console.log("Added item.", this.state.todoInput);

    const todolist = this.state.todoList.slice();
    this.setState({ todoList: todolist.concat([this.state.todoInput]) });
  }

  render() {
    const todoItems = this.state.todoList.map((elt) => {
      return ( <li>{elt}</li> )
    });

    return (
      <div>
        <div>This is the Todo component: {this.props.testprop}</div>
        <input type="text" value={this.state.todoInput} onChange={(e) => this.handleChange(e) } />
        <button onClick={() => this.addItem()}>Add</button>
        <div>
          <ul>
            {todoItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default Todo;
