import React, { Component } from 'react';
import './Calculator.css';

class CalcButton extends Component {

  render() {
    return (
      <div onClick={() => this.props.clickHandler(this.props.text)}>
        {this.props.text}
      </div>
    )
  }
}

class Calculator extends Component {
  constructor(props){
    super(props);
    this.state = {
      result: ""
    };
  }

  buttonHandler(val) {
    console.log("buttonHandler", val, Number.isInteger(val));

    let res = "";
    if (val === "=") {
      res = eval(this.state.result);
    } else if (val !== "clear") {
      res = this.state.result + val;
    }
    this.setState({
      result: res
    });
  }

  render() {
    return (
      <div>
        <div className="result-panel">
          {this.state.result}
        </div>
        <table>
        <tbody>
          <tr>
            <td colSpan="2"><CalcButton text={"clear"} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={'*'} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={'/'} clickHandler={(v) => this.buttonHandler(v)}/></td>
          </tr>
          <tr>
            <td><CalcButton text={1} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={2} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={3} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={'+'} clickHandler={(v) => this.buttonHandler(v)}/></td>
          </tr>
          <tr>
            <td><CalcButton text={4} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={5} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={6} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={'-'} clickHandler={(v) => this.buttonHandler(v)}/></td>
          </tr>
          <tr>
            <td><CalcButton text={7} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={8} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={9} clickHandler={(v) => this.buttonHandler(v)}/></td>
            <td><CalcButton text={'='} clickHandler={(v) => this.buttonHandler(v)}/></td>
          </tr>
        </tbody>
        </table>
      </div>
    );
  }
}

export default Calculator;
