import React, { Component } from 'react';
import './TeamRoster.css';

class TeamRoster extends Component {
    constructor(props){
        super(props);
        this.state = {
            roster: [],
            currentPlayerViewing: null
        };
    }

  // componentWillMount(){}
    componentDidMount() {
        fetch("http://localhost:8080/player")
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    roster: result
                });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }

    // componentWillUnmount(){}
    // componentWillReceiveProps(){}
    // shouldComponentUpdate(){}
    // componentWillUpdate(){}
    // componentDidUpdate(){}

    viewPlayerDetails(player) {
        this.setState({ currentPlayerViewing: player });
    }

    render() {

        const rosterRows = this.state.roster.map((player) => {
            return (
                <tr key={player.id}>
                    <td>{player.firstName} {player.lastName}</td>
                    <td>{player.position}</td>
                    <td><button onClick={() => this.viewPlayerDetails(player)}>Details</button></td>
                    {/*
                    <td>{player.age}</td>
                    <td>{player.height}</td>
                    <td>{player.weight}</td>
                    <td>{player.college}</td>
                    <td>{player.salary}</td>*/
                    }
                </tr>
            )
        })

        return (
        <div>
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Position</th>
                            <th></th>
                            {/*<th>Height</th>
                            <th>Weight</th>
                            <th>College</th>
                            <th>Salary</th>*/
                            }
                        </tr>
                    </thead>
                    <tbody>{rosterRows}</tbody>
                </table>
            </div>
            {
            (this.state.currentPlayerViewing !== null) ? <PlayerDetail player={this.state.currentPlayerViewing} /> : null
            }
        </div>
        );
    }
}

class PlayerDetail extends Component {
    constructor(props) {
      super(props);
      this.state = {

      }
    }

    render(){
        return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Age</th>
                        <th>Height</th>
                        <th>Weight</th>
                        <th>College</th>
                        <th>Salary</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{this.props.player.firstName} {this.props.player.lastName}</td>
                        <td>{this.props.player.position}</td>
                        <td>{this.props.player.age}</td>
                        <td>{this.props.player.height}</td>
                        <td>{this.props.player.weight}</td>
                        <td>{this.props.player.college}</td>
                        <td>{this.props.player.salary}</td>
                    </tr>
                </tbody>
            </table>
        </div>
        );
    }
  }

export default TeamRoster;
